    short m_mic;
    
    static const short        m_search[28];   
    static const short        m_fromISO[MAXMARKETID]; 
    static const short        m_toISO[NUMMARKETID];
    static const char * const m_codes[NUMMARKETID];
    static const char * const m_fullNames[NUMMARKETID];
